    public void images() throws MalformedURLException, IOException {
        try {
            String ruta = "./data/mix.txt";
            String text = "";
            String[] mixs = null;
            int n = 0;

            mix = cm.ReadMix(ruta);
            for (int i = 0; i < mix.length; i++) {
                if (mix[i] != null) {
                    n++;
                }
            }
            namemixs = new String[n];

            for (int i = 0; i < n; i++) {
                mixs = mix[i].split(",");
                this.jComboBox1.addItem(mixs[mixs.length - 1]);
            }
        } catch (FileNotFoundException ex) {
            Logger.getLogger(JFrameMain.class.getName()).log(Level.SEVERE, null, ex);
        }

        String fichero = "./data/archivo.txt";
        String url = "";
        try {
            FileReader fr = new FileReader(fichero);
            BufferedReader br = new BufferedReader(fr);

            String linea;
            while ((linea = br.readLine()) != null) {
                url = url + linea;
            }
            fr.close();

        } catch (Exception e) {
            System.out.println("Excepcion leyendo fichero " + fichero + ": " + e);
        }

        String[] parts = url.split("data:image/png;base64,");

        for (int j = 1; j < parts.length; j++) {
            //convert base64 string to binary data
            byte[] data = DatatypeConverter.parseBase64Binary(parts[j]);
            String path = "./data/image" + (j - 1) + ".png";
            File file = new File(path);
            try (OutputStream outputStream = new BufferedOutputStream(new FileOutputStream(file))) {
                outputStream.write(data);
            } catch (IOException e) {
                e.printStackTrace();
            }
            ImageIcon icon = new ImageIcon("C:\\Users\\jessi\\Documents\\NetBeansProjects\\Desktop\\data\\image" + (j - 1) + ".png");
            JLabel etiqueta = new JLabel("Etiqueta " + indice);
            etiqueta.setText(null);
            etiqueta.setSize(300, 300);
            Icon icono = new ImageIcon(icon.getImage().getScaledInstance(etiqueta.getWidth(), etiqueta.getHeight(), Image.SCALE_DEFAULT));
            etiqueta.setIcon(icono);
            this.jPanel1.add(etiqueta);
            imagenes.add(etiqueta);
            JLabel titulo = new JLabel("Parqueo: " + indice);
            this.jPanel2.add(titulo);
            JComboBox combo = new JComboBox();
            combo.addItem("Ocupado");
            combo.addItem("Libre");
            estados.add(combo);
            this.jPanel2.add(combo);
            indice++;
            this.jPanel1.updateUI();
            this.jPanel2.updateUI();

        }

    }

